import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import API_BASE_URL from '../config';

function FloatingAvatars() {
  const canvasRef = useRef(null);
  const [avatars, setAvatars] = useState([]);
  const ballsRef = useRef([]);

  useEffect(() => {
    // Carica gli avatar dal backend
    const loadAvatars = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/api/events/names`);
        const people = response.data.filter(person => person.avatar_url);
        setAvatars(people);
      } catch (error) {
        console.error('Errore caricamento avatar:', error);
      }
    };

    loadAvatars();
  }, []);

  useEffect(() => {
    if (avatars.length === 0) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Imposta dimensioni canvas
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // Carica immagini avatar
    const images = [];
    let imagesLoaded = 0;

    avatars.forEach((person, index) => {
      const img = new Image();
      img.crossOrigin = 'anonymous';
      img.src = person.avatar_url;
      img.onload = () => {
        imagesLoaded++;
        if (imagesLoaded === avatars.length) {
          initBalls();
        }
      };
      img.onerror = () => {
        imagesLoaded++;
        if (imagesLoaded === avatars.length) {
          initBalls();
        }
      };
      images.push(img);
    });

    // Inizializza palline
    const initBalls = () => {
      ballsRef.current = [];
      const numBalls = Math.min(avatars.length, 15); // Max 15 avatar

      for (let i = 0; i < numBalls; i++) {
        const size = 60 + Math.random() * 40; // Dimensione casuale 60-100px
        ballsRef.current.push({
          x: Math.random() * (canvas.width - size),
          y: Math.random() * (canvas.height - size),
          vx: (Math.random() - 0.5) * 3, // Velocità X
          vy: (Math.random() - 0.5) * 3, // Velocità Y
          size: size,
          img: images[i % images.length],
          rotation: Math.random() * Math.PI * 2,
          rotationSpeed: (Math.random() - 0.5) * 0.02
        });
      }

      animate();
    };

    // Aggiungi nell'useEffect dopo initBalls():

    canvas.addEventListener('click', (e) => {
    const rect = canvas.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const clickY = e.clientY - rect.top;

    ballsRef.current.forEach(ball => {
        const dx = (ball.x + ball.size / 2) - clickX;
        const dy = (ball.y + ball.size / 2) - clickY;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < ball.size / 2) {
        // Impulso verso fuori
        const angle = Math.atan2(dy, dx);
        ball.vx = Math.cos(angle) * 10;
        ball.vy = Math.sin(angle) * 10;
        ball.rotationSpeed = (Math.random() - 0.5) * 0.2;
        }
    });
    });

    // Animazione
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      ballsRef.current.forEach(ball => {
        // Aggiorna posizione
        ball.x += ball.vx;
        ball.y += ball.vy;

        // Rimbalzo sui bordi
        if (ball.x <= 0 || ball.x + ball.size >= canvas.width) {
          ball.vx *= -1;
          ball.x = Math.max(0, Math.min(ball.x, canvas.width - ball.size));
        }
        if (ball.y <= 0 || ball.y + ball.size >= canvas.height) {
          ball.vy *= -1;
          ball.y = Math.max(0, Math.min(ball.y, canvas.height - ball.size));
        }

        // Aggiorna rotazione
        ball.rotation += ball.rotationSpeed;

        // Disegna avatar circolare
        ctx.save();
        ctx.translate(ball.x + ball.size / 2, ball.y + ball.size / 2);
        ctx.rotate(ball.rotation);

        // Clip circolare
        ctx.beginPath();
        ctx.arc(0, 0, ball.size / 2, 0, Math.PI * 2);
        ctx.closePath();
        ctx.clip();

        // Disegna immagine
        ctx.drawImage(
          ball.img,
          -ball.size / 2,
          -ball.size / 2,
          ball.size,
          ball.size
        );

        ctx.restore();

        // Bordo bianco con ombra
        ctx.save();
        ctx.shadowColor = 'rgba(0, 0, 0, 0.3)';
        ctx.shadowBlur = 10;
        ctx.strokeStyle = 'rgba(255, 255, 255, 0.8)';
        ctx.lineWidth = 3;
        ctx.beginPath();
        ctx.arc(ball.x + ball.size / 2, ball.y + ball.size / 2, ball.size / 2, 0, Math.PI * 2);
        ctx.stroke();
        ctx.restore();
      });

      requestAnimationFrame(animate);
    };

    return () => {
      window.removeEventListener('resize', resizeCanvas);
    };
  }, [avatars]);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: -1,
        pointerEvents: 'none'
      }}
    />
  );
}

export default FloatingAvatars;
