import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function InsertEvent() {
  const navigate = useNavigate();
  const [names, setNames] = useState([]);
  const [eventTypes, setEventTypes] = useState([]);
  const [formData, setFormData] = useState({
    person_name: '',
    event_type_id: '',
    declarer_name: '',
    description: ''
  });
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const [namesRes, typesRes] = await Promise.all([
        axios.get('/api/events/names'),
        axios.get('/api/events/types')
      ]);
      setNames(namesRes.data);
      setEventTypes(typesRes.data);
    } catch (error) {
      console.error('Errore nel caricamento dei dati:', error);
      setMessage({ type: 'error', text: 'Errore nel caricamento dei dati' });
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage(null);

    const data = new FormData();
    data.append('person_name', formData.person_name);
    data.append('event_type_id', formData.event_type_id);
    data.append('declarer_name', formData.declarer_name);
    data.append('description', formData.description);
    if (file) {
      data.append('media', file);
    }

    try {
      await axios.post('/api/events', data, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setMessage({ type: 'success', text: 'Evento inserito con successo!' });
      setFormData({
        person_name: '',
        event_type_id: '',
        declarer_name: '',
        description: ''
      });
      setFile(null);
      setTimeout(() => navigate('/'), 2000);
    } catch (error) {
      console.error('Errore nell\'inserimento:', error);
      setMessage({ type: 'error', text: 'Errore nell\'inserimento dell\'evento' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>‚ûï Inserisci Evento</h1>

      {message && (
        <div className={`alert alert-${message.type === 'success' ? 'success' : 'error'}`}>
          {message.text}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome Persona (a cui succede l'evento):</label>
          <select
            name="person_name"
            value={formData.person_name}
            onChange={handleInputChange}
            required
          >
            <option value="">Seleziona...</option>
            {names.map(name => (
              <option key={name.id} value={name.name}>{name.name}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Tipo di Evento:</label>
          <select
            name="event_type_id"
            value={formData.event_type_id}
            onChange={handleInputChange}
            required
          >
            <option value="">Seleziona...</option>
            {eventTypes.map(type => (
              <option key={type.id} value={type.id}>
                {type.name} ({type.points} punti)
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Nome Dichiarante (chi inserisce l'evento):</label>
          <select
            name="declarer_name"
            value={formData.declarer_name}
            onChange={handleInputChange}
            required
          >
            <option value="">Seleziona...</option>
            {names.map(name => (
              <option key={name.id} value={name.name}>{name.name}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Descrizione (opzionale):</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            placeholder="Aggiungi dettagli sull'evento..."
          />
        </div>

        <div className="form-group">
          <label>Allega Foto/Video/Audio (opzionale):</label>
          <input
            type="file"
            accept="image/*,video/*,audio/*"
            onChange={handleFileChange}
            className="file-input"
          />
        </div>

        <div className="button-group">
          <button type="submit" disabled={loading}>
            {loading ? '‚è≥ Invio...' : 'üì§ Invia Evento'}
          </button>
          <button type="button" className="secondary" onClick={() => navigate('/')}>
            ‚Üê Indietro
          </button>
        </div>
      </form>
    </div>
  );
}

export default InsertEvent;
